package op;

public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 9, y = 8;   
		  
		System.out.println("x & y = " + (x & y)); 
		System.out.println("x | y = " + (x | y));
		System.out.println("x ^ y = " + (x ^ y));
		System.out.println("~x = " + ~(x));
		

	}

}
