package op;

public class Relational {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=50;
		int z=90;
		
		System.out.println("x>z: " +(x>z));
		System.out.println("x<z: " +(x<z));
		System.out.println("x>=z: " +(x>=z));
		System.out.println("x<=z: " +(x<=z));
		System.out.println("x!=z: " +(x!=z));
		System.out.println("x==z: " +(x==z));

	}

}
