package operator;

public class IncDec {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, x;

	     a = 10;
	     x= 70;

	     System.out.println("a: "+a);
	     System.out.println("x: "+x);
	     System.out.println("a: "+(--a));
	     System.out.println("x: "+(x--));
	     System.out.println("a: "+(++a));
	     System.out.println("x: "+(x++));
	     System.out.println("a: "+(a++));
	     System.out.println("a: "+(a--));
	     System.out.println("x: "+(--x));
	     System.out.println("x: "+(++x));
	     

	}
}
